var FormValidation = function() {
    //debugger
    r = function() {
        var e = $("#form_sample_2"),
        r = $(".alert-danger", e),
        i = $(".alert-success", e);
        e.validate({
            errorElement: "span",
            errorClass: "help-block help-block-error",
            focusInvalid: !1,
            ignore: "",
            rules: {
                member_name: {
                    minlength: 4,
                    required: !0,
                    remote: {
                        url: "/admin/memberCheck/",      
                        type: "post",                
                        data: {                     
                            member_name: function() {
                                return $("#member_name").val();
                            }
                        }
                    }


                },
                member_pwd: {
                    minlength: 6,
                    required: !0 
                }
                ,
                member_pwd_2: {
                    minlength: 6,
                    required: !0 ,
                    equalTo:'#member_pwd'
                }
                ,
                member_integral: {
                    number: !0 ,
                }
                
            },
            messages:{
                member_name: {
                    required: "请输入用户名",
                    minlength:"请输入至少4个字符",
                    remote: "账号已存在，请重新输入"
                },
                member_pwd: {
                    required: "请输入密码",
                    minlength:"请输入至少6个字符"
                }
                ,
                member_pwd_2: {
                    required: "请再次输入密码",
                    minlength:"请输入至少6个字符",
                    equalTo:'两次输入的密码不一致'
                }
                ,
                member_integral: {
                    number: "请输入数字"
                  
                }
            }
            ,
            invalidHandler: function(e, t) {
                i.hide(),
                r.show(),
                App.scrollTo(r, -200)
            },
            errorPlacement: function(e, r) {

                var i = $(r).parent(".input-icon").children("i");
                i.removeClass("fa-check").addClass("fa-warning"),
                i.attr("data-original-title", e.text()).tooltip({
                    container: "body"
                })
            },
            highlight: function(e) {
                $(e).closest(".form-group").removeClass("has-success").addClass("has-error")
            },
            unhighlight: function(e) {},
            success: function(e, r) {
                var i = $(r).parent(".input-icon").children("i");
                $(r).closest(".form-group").removeClass("has-error").addClass("has-success"),
                i.removeClass("fa-warning").addClass("fa-check")
            },
            submitHandler: function(e) {
                i.show(),
                r.hide(),
                e[0].submit()
            }
        })
    } 
     
    return {
        init: function() {
            
            r() 
        }
    }
} ();

jQuery(document).ready(function() {
    FormValidation.init()
});