var ComponentsColorPickers = function () {
    var t = function () {
        jQuery().colorpicker && ($(".colorpicker-default").colorpicker({
            format: "hex"
        }), $(".colorpicker-rgba").colorpicker())
    },
    o = function () {
        $(".demo").each(function () {
            $(this).minicolors({
                control: $(this).attr("data-control") || "hue",
                defaultValue: $(this).attr("data-defaultValue") || "",
                inline: "true" === $(this).attr("data-inline"),
                letterCase: $(this).attr("data-letterCase") || "lowercase",
                opacity: $(this).attr("data-opacity"),
                position: $(this).attr("data-position") || "bottom left",
                change: function (t, o) {
                    $("input[name='article_title']").css("color", $("input[name='article_title_color']").val())
                    // t && (o && (t += ", " + o))
                   //return false
                },
                theme: "bootstrap"
            })
        })
    };
    return {
        init: function () {
            o(),
            t()
        }
    }
} ();
jQuery(document).ready(function () {
    ComponentsColorPickers.init()
});